package java20250123;

public class java432134 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      //비트 논리 연산자
		System.out.println(27&13);//and 연산

		System.out.println(27|13);//or연산
		
		System.out.println(27^13);//xor연산
		System.out.println(~-14);//not 연산
		//3이란 숫자에 7의 보수를 취한다 4
		//2란 숫자에 10의 보수는 8 10이 되기에 부족한 숫자가 몇인지 
		
		//0.1 2진수 
		System.out.println(21<<2);// <<쉬프트 연산자
		System.out.println(17>3);// >>쉬프트 연산자
	}

}
